// -------------------------------// adapted from Kevin T. Manley// CSE 593// -------------------------------package ResImpl;public class Hotel extends ReservableItem {	private static final long serialVersionUID = 8539138989703034993L;	public Hotel(String location, int count, int price) {		super(location, count, price);	}	public String getKey() {		return Hotel.getKey(getLocation());	}	public static String getKey(String location) {		String s = "room-" + location;		return s.toLowerCase();	}}